// Parse follow-up request
const requestData = items[0].json;
const followupType = requestData.type; // 'single', 'bulk', 'scheduled'
const applicationIds = Array.isArray(requestData.application_ids) ? 
  requestData.application_ids : [requestData.application_id];
const userId = requestData.user_id;
const templateType = requestData.template_type || '1_week';

console.log(`Processing ${followupType} follow-up for ${applicationIds.length} applications`);

return [{
  json: {
    type: followupType,
    application_ids: applicationIds,
    user_id: userId,
    template_type: templateType,
    timestamp: new Date().toISOString()
  }
}];

// Generate personalized follow-up emails
const userProfile = items[0].json;
const applications = items[1].json.applications || [];
const template = items[2].json.template || {};

const followupEmails = [];

// Default templates if none found
const defaultTemplates = {
  '1_week': {
    subject: 'Following up on my application - {{job_title}} at {{company_name}}',
    body: `Dear Hiring Manager,

I hope this email finds you well. I wanted to follow up on my application for the {{job_title}} position at {{company_name}} that I submitted {{days_since_application}} days ago.

I remain very interested in this opportunity and believe my background in {{key_skills}} would be a great fit for your team. I'm particularly excited about {{company_name}}'s mission and would love to contribute to your success.

I would welcome the opportunity to discuss how my {{years_experience}} years of experience in {{relevant_field}} can benefit your organization.

Thank you for your time and consideration. I look forward to hearing from you.

Best regards,
{{user_name}}
{{user_email}}
{{user_phone}}`
  },
  '2_week': {
    subject: 'Checking in - {{job_title}} position at {{company_name}}',
    body: `Hello,

I hope you're doing well. I'm writing to check in regarding my application for the {{job_title}} position at {{company_name}}.

I submitted my application {{days_since_application}} days ago and wanted to reiterate my strong interest in joining your team. Since applying, I've been following {{company_name}}'s recent developments and I'm even more excited about the possibility of contributing to your goals.

If you need any additional information or would like to schedule a conversation, please don't hesitate to reach out. I'm available at your convenience.

Thank you for your consideration.

Warm regards,
{{user_name}}`
  },
  'custom': {
    subject: 'Re: {{job_title}} Application',
    body: 'Custom message based on user input'
  }
};

// Use template from database or default
const emailTemplate = template.subject_template ? {
  subject: template.subject_template,
  body: template.body_template
} : defaultTemplates[items[0].json.template_type] || defaultTemplates['1_week'];

// Generate emails for each application
applications.forEach(application => {
  const daysSinceApplication = Math.floor(
    (new Date() - new Date(application.created_at)) / (1000 * 60 * 60 * 24)
  );

  // Replace template variables
  const variables = {
    user_name: userProfile.full_name || userProfile.first_name + ' ' + userProfile.last_name,
    user_email: userProfile.email,
    user_phone: userProfile.phone || '',
    job_title: application.job_title,
    company_name: application.company_name,
    days_since_application: daysSinceApplication,
    key_skills: userProfile.key_skills ? userProfile.key_skills.slice(0, 3).join(', ') : 'data science, machine learning',
    years_experience: userProfile.years_experience || '5+',
    relevant_field: application.job_title.toLowerCase().includes('data') ? 'data science' : 
                   application.job_title.toLowerCase().includes('software') ? 'software development' : 
                   'technology'
  };

  let personalizedSubject = emailTemplate.subject;
  let personalizedBody = emailTemplate.body;

  // Replace all variables
  Object.keys(variables).forEach(key => {
    const regex = new RegExp(`{{${key}}}`, 'g');
    personalizedSubject = personalizedSubject.replace(regex, variables[key]);
    personalizedBody = personalizedBody.replace(regex, variables[key]);
  });

  followupEmails.push({
    application_id: application.id,
    to_email: application.contact_email || `careers@${application.company_name.toLowerCase().replace(/\s+/g, '')}.com`,
    subject: personalizedSubject,
    body: personalizedBody,
    application: application,
    variables: variables
  });
});

console.log(`Generated ${followupEmails.length} personalized follow-up emails`);

return [{
  json: {
    user_profile: userProfile,
    emails: followupEmails,
    template_used: emailTemplate,
    generation_timestamp: new Date().toISOString()
  }
}];

// Split emails into individual items for processing
const emailData = items[0].json;
const emails = emailData.emails;

return emails.map(email => ({
  json: {
    ...email,
    user_profile: emailData.user_profile,
    template_used: emailData.template_used
  }
}));

