// Parse incoming job search request
const requestData = items[0].json;
const userId = requestData.user_id;
const searchCategories = requestData.categories || [];
const jobCount = requestData.job_count || 15;
const userPreferences = requestData.user_preferences || {};

// Smart distribution of jobs across categories
const jobsPerCategory = Math.ceil(jobCount / searchCategories.length);

// Prepare search parameters
const searchParams = {
  user_id: userId,
  categories: searchCategories,
  jobs_per_category: jobsPerCategory,
  total_jobs: jobCount,
  location: userPreferences.location || 'Remote',
  salary_min: userPreferences.salary_min || 50000,
  salary_max: userPreferences.salary_max || 150000,
  experience_level: userPreferences.experience_level || 'mid',
  remote_preference: userPreferences.remote_preference || 'remote'
};

console.log('Search parameters prepared:', searchParams);

return [{
  json: searchParams
}];

// Combine and process job results from all sources
const indeedJobs = items[0]?.json?.results || [];
const glassdoorJobs = items[1]?.json?.response?.jobs || [];
const linkedinJobs = items[2]?.json?.elements || [];

const allJobs = [];

// Process Indeed jobs
indeedJobs.forEach(job => {
  allJobs.push({
    source: 'Indeed',
    job_title: job.jobtitle,
    company_name: job.company,
    location: job.formattedLocation,
    job_url: job.url,
    job_description: job.snippet,
    salary_range: job.formattedSalaryMin && job.formattedSalaryMax ? 
      `$${job.formattedSalaryMin} - $${job.formattedSalaryMax}` : 'Not specified',
    date_posted: job.date,
    remote_option: job.formattedLocation.includes('Remote') ? 'Remote' : 'On-site',
    urgency_level: 'medium',
    match_percentage: calculateMatchPercentage(job, items[0].json.categories)
  });
});

// Process Glassdoor jobs
glassdoorJobs.forEach(job => {
  allJobs.push({
    source: 'Glassdoor',
    job_title: job.jobTitle,
    company_name: job.employer.name,
    location: job.location,
    job_url: job.jobUrl,
    job_description: job.jobDescription,
    salary_range: job.estimatedSalary ? job.estimatedSalary : 'Not specified',
    date_posted: job.discoverDate,
    remote_option: job.location.includes('Remote') ? 'Remote' : 'On-site',
    urgency_level: 'medium',
    match_percentage: calculateMatchPercentage(job, items[0].json.categories),
    company_rating: job.employer.overallRating
  });
});

// Process LinkedIn jobs
linkedinJobs.forEach(job => {
  allJobs.push({
    source: 'LinkedIn',
    job_title: job.title,
    company_name: job.companyDetails?.company?.name || 'Unknown',
    location: job.formattedLocation,
    job_url: `https://linkedin.com/jobs/view/${job.dashEntityUrn.split(':').pop()}`,
    job_description: job.description?.text || '',
    salary_range: 'Not specified',
    date_posted: new Date(job.listedAt).toISOString(),
    remote_option: job.workRemoteAllowed ? 'Remote' : 'On-site',
    urgency_level: 'medium',
    match_percentage: calculateMatchPercentage(job, items[0].json.categories)
  });
});

// Apply smart filters
function applySmartFilters(jobs, preferences) {
  return jobs.filter(job => {
    // Salary filter
    if (preferences.salary_min && job.salary_range !== 'Not specified') {
      const salaryMatch = extractSalaryRange(job.salary_range);
      if (salaryMatch && salaryMatch.min < preferences.salary_min) {
        return false;
      }
    }

    // Remote preference filter
    if (preferences.remote_preference === 'remote' && job.remote_option !== 'Remote') {
      return false;
    }

    // Experience level filter (basic implementation)
    if (preferences.experience_level === 'entry' && 
        (job.job_title.toLowerCase().includes('senior') || 
         job.job_title.toLowerCase().includes('lead'))) {
      return false;
    }

    return true;
  });
}

// Calculate match percentage based on job requirements vs user skills
function calculateMatchPercentage(job, categories) {
  const jobText = `${job.jobTitle || job.job_title || ''} ${job.snippet || job.jobDescription || job.description?.text || ''}`.toLowerCase();
  let matches = 0;

  categories.forEach(category => {
    if (jobText.includes(category.toLowerCase())) {
      matches++;
    }
  });

  return Math.min((matches / categories.length) * 100, 100);
}

// Extract salary range from string
function extractSalaryRange(salaryStr) {
  const matches = salaryStr.match(/\$(\d+)(?:,?(\d+))?.*?\$(\d+)(?:,?(\d+))?/);
  if (matches) {
    const min = parseInt(matches[1] + (matches[2] || ''));
    const max = parseInt(matches[3] + (matches[4] || ''));
    return { min, max };
  }
  return null;
}

// Apply filters and sort by relevance
const filteredJobs = applySmartFilters(allJobs, items[0].json);
const sortedJobs = filteredJobs.sort((a, b) => b.match_percentage - a.match_percentage);

// Limit to requested number
const finalJobs = sortedJobs.slice(0, items[0].json.total_jobs);

console.log(`Found ${allJobs.length} total jobs, filtered to ${finalJobs.length}`);

return [{
  json: {
    user_id: items[0].json.user_id,
    jobs: finalJobs,
    search_metadata: {
      total_found: allJobs.length,
      filtered_count: finalJobs.length,
      sources_used: ['Indeed', 'Glassdoor', 'LinkedIn'],
      search_timestamp: new Date().toISOString(),
      categories_searched: items[0].json.categories
    }
  }
}];

